-- Update script to set user_id = 1 and add image data to production database
-- Run this script in DBeaver after running the clean-production-import.sql

-- Step 1: Set all properties to user_id = 1
UPDATE open_houses SET user_id = 1 WHERE user_id IS NULL;

-- Step 2: Update specific properties with their image data
-- Property: 45 Diamond Drive
UPDATE open_houses 
SET image_data = 'data:image/png;base64,'
WHERE address = '45 Diamond Drive, Palm Coast, FL 32164';

-- Property: 36 Wasserman Dr
UPDATE open_houses 
SET image_data = 'data:image/jpeg;base64,'
WHERE address = '36 Wasserman Dr, Palm Coast, FL 32164';

-- Property: 6 Seoane Ct  
UPDATE open_houses 
SET image_data = 'data:image/jpeg;base64,'
WHERE address = '6 Seoane Ct, Palm Coast, FL 32164';

-- Property: 1 Westgate Ln
UPDATE open_houses 
SET image_data = 'data:image/jpeg;base64,'
WHERE address = '1 Westgate Ln Palm Coast Fl 32164';

-- Step 3: Verify the updates
SELECT COUNT(*) as properties_with_user_id FROM open_houses WHERE user_id = 1;
SELECT COUNT(*) as properties_with_images FROM open_houses WHERE image_data IS NOT NULL;

-- Step 4: Show sample of updated data
SELECT id, address, CASE WHEN image_data IS NULL THEN 'No Image' ELSE 'Has Image' END as image_status, user_id 
FROM open_houses 
ORDER BY id 
LIMIT 5;